INSERT INTO `book` (`id`, `book_name`, `book_slug`, `book_description`, `book_image`, `book_status`, `book_view`, `created_at`, `updated_at`) VALUES
(1, 'Truyện kể bé nghe', 'truyen-ke-be-nghe', '<p>Đ&acirc;y s&aacute;ch bao gồm nhiều chương kể về c&aacute;c c&acirc;u chuyện c&oacute; &iacute;ch cho b&eacute; nghe</p>', 'truyen-ke-be-nghe_093647181223.jpg', 1, 10, '2023-12-18 09:24:50', '2023-12-18 09:36:47'),
(2, 'Truyện ngắn Nam Cao', 'truyen-ngan-nam-cao', '<p>Đ&acirc;y l&agrave; s&aacute;ch Văn học Việt Nam</p>', 'truyen-ngan-nam-cao_094018181223.jpg', 1, 4, '2023-12-18 09:24:50', '2023-12-18 09:50:09'),
(3, 'Tuyện cười', 'tuyen-cuoi', '<p>Đ&acirc;y l&agrave; cười gi&uacute;p bạn thư gi&atilde;n, giảm stress</p>', 'tuyen-cuoi_172110181223.jpeg', 1, 1, '2023-12-18 09:24:50', '2023-12-18 17:21:10'),
(4, 'Việt Nam sử lược', 'viet-nam-su-luoc', '<p>Việt Nam sử lược &ndash; xuất bản năm 1919 của Trần Trọng Kim</p>', 'viet-nam-su-luoc_172214181223.jpeg', 1, 0, '2023-12-18 09:24:50', '2023-12-18 17:22:14'),
(5, 'Machine Learning Cơ Bản', 'machine-learning-co-ban', '<p>Machine Learning l&agrave; một tập con của AI. Theo định nghĩa của Wikipedia,&nbsp;Machine learning is the subfield of computer science that &#39;gives computers the ability to learn without being explicitly programmed&#39;</p>', 'machine-learning-co-ban_172329181223.jpeg', 1, 0, '2023-12-18 09:24:50', '2023-12-18 17:23:29'),
(6, 'Ngữ Pháp Tiếng Anh Ôn Thi Toeic', 'ngu-phap-tieng-anh-on-thi-toeic', '<p>Đ&acirc;y l&agrave; &#39;Hệ thống ngữ ph&aacute;p&#39; chuẩn của Bộ gi&aacute;o dục ban h&agrave;ng trong loạt hệ thống kiến thức trọng t&acirc;m học&nbsp;&ocirc;n&nbsp;Toeic hiệu quả.&nbsp;</p>', 'ngu-phap-tieng-anh-on-thi-toeic_172421181223.png', 1, 1, '2023-12-18 09:24:50', '2023-12-18 17:24:21'),
(7, 'Giải thích Thành ngữ - Tục ngữ', 'giai-thich-thanh-ngu-tuc-ngu', '<p>Giup giải th&iacute;ch th&agrave;nh ngữ tục ngữ ch&iacute;nh x&aacute;c</p>', 'giai-thich-thanh-ngu-tuc-ngu_172510181223.jpeg', 1, 2, '2023-12-18 09:24:50', '2023-12-18 17:25:10'),
(8, 'Mùa Thu Lá Bay', 'mua-thu-la-bay', '<p>M&ugrave;a Thu L&aacute; Bay l&agrave; một tiểu thuyết l&atilde;ng mạn của t&aacute;c giả Quỳnh Dao, được xuất bản lần đầu ti&ecirc;n v&agrave;o năm 1966.</p>', 'mua-thu-la-bay_172607181223.jpeg', 1, 0, '2023-12-18 09:24:50', '2023-12-18 17:26:07'),
(9, 'Hoa Hồng Thép', 'hoa-hong-thep', '<p>Cuốn tiểu thuyết&nbsp;Hoa Hồng Th&eacute;p&nbsp;của t&aacute;c giả Tr&agrave; Hựu Thanh xoay quanh vụ &aacute;n giết người h&agrave;ng loạt ở Th&aacute;i Lan. Kẻ giết người được đặt biệt danh l&agrave; &#39;Hoa Hồng Th&eacute;p&#39; v&igrave; hắn chỉ giết những người phụ nữ đẹp, trẻ trung v&agrave; gi&agrave;u c&oacute;.</p>', 'hoa-hong-thep_174553181223.jpeg', 1, 0, '2023-12-18 09:24:50', '2023-12-18 17:46:21'),
(10, 'Du lịch nước ngoài', 'du-lich-nuoc-ngoai', '<p>Thay v&igrave; ảo tưởng lấy bản th&acirc;n m&igrave;nh l&agrave;m khu&ocirc;n v&agrave;ng thước ngọc để m&agrave; đo thế giới, văn h&oacute;a Nhật Bản hướng người ta đến việc tự hiểu m&igrave;nh để t&igrave;m cho m&igrave;nh một chỗ đứng ph&ugrave; hợp trong thế giới. Để thế giới l&agrave; vườn hoa mu&ocirc;n ngh&igrave;n hương sắc&hellip;</p>', 'du-lich-nuoc-ngoai_174800181223.jpeg', 1, 0, '2023-12-18 09:24:50', '2023-12-18 17:48:00'),
(11, 'Trí Tuệ Do Thái', 'tri-tue-do-thai', '<p>Cuốn s&aacute;ch m&agrave; bạn đang cầm tr&ecirc;n tay l&agrave; kết quả của mối nh&acirc;n duy&ecirc;n kỳ ngộ giữa C&ocirc;ng ty S&aacute;ch Alpha v&agrave; t&aacute;c giả của n&oacute; l&agrave; Eran Katz &ndash; một nh&agrave; văn, học giả người Israel, cũng l&agrave; người nổi tiếng với kỷ lục Guinness về khả năng nhớ lại được một d&atilde;y số c&oacute; đến 500 chữ số sau khi được nghe chỉ một lần</p>', 'tri-tue-do-thai_171810181223.jpeg', 1, 0, '2023-12-18 17:18:10', '2023-12-18 17:21:26'),
(12, 'Trí Tuệ', 'tri-tue', '<p>Tr&iacute; Tuệ Do Th&aacute;i kh&ocirc;ng dừng lại ở giới hạn của một cuốn s&aacute;ch triết l&yacute; hay kỹ năng. Th&ocirc;ng qua Jerome, một kẻ l&ocirc;ng b&ocirc;ng th&iacute;ch la c&agrave;, t&aacute;c giả đưa người đọc v&agrave;o một chuyến kh&aacute;m ph&aacute; về tr&iacute; tuệ của người Do Th&aacute;i, từ đ&oacute; khơi ra những giới hạn để người đọc c&oacute; thể tự khai ph&aacute; tr&iacute; tuệ bản th&acirc;n với Năm nguy&ecirc;n tắc v&agrave; Mười lăm gợi &yacute;. Đ&acirc;y sẽ l&agrave; những b&agrave;i học qu&yacute; gi&aacute; d&agrave;nh cho những ai muốn tồn tại v&agrave; ph&aacute;t triển mạnh mẽ, kh&ocirc;ng chỉ với con đường th&agrave;nh c&ocirc;ng của ri&ecirc;ng m&igrave;nh</p>', 'tri-tue_171952181223.jpeg', 1, 0, '2023-12-18 17:19:52', '2023-12-18 17:21:24'),
(13, 'Số Đỏ - Nguyên Hồng', 'so-do-nguyen-hong', '<p>Số Đỏ l&agrave; một tiểu thuyết nổi tiếng của nh&agrave; văn Nguyễn Hồng, xuất bản lần đầu v&agrave;o năm 1936. T&aacute;c phẩm n&agrave;y tập trung phản &aacute;nh sự thối n&aacute;t trong x&atilde; hội v&agrave; ch&iacute;nh trị Việt Nam thời kỳ đầu thế kỷ 20</p>', 'so-do-nguyen-hong_173203181223.jpeg', 1, 0, '2023-12-18 17:32:03', '2023-12-18 17:44:48'),
(14, 'Những Người Tình - Du Tử Lê', 'nhung-nguoi-tinh-du-tu-le', '<p>T&aacute;c phẩm của Du Tử L&ecirc; kể về c&acirc;u chuyện t&igrave;nh cảm phức tạp, lồng gh&eacute;p v&agrave;o đ&oacute; l&agrave; những kh&iacute;a cạnh x&atilde; hội v&agrave; t&acirc;m l&yacute; của nh&acirc;n vật</p>', 'nhung-nguoi-tinh-du-tu-le_173330181223.jpeg', 1, 0, '2023-12-18 17:33:30', '2023-12-18 17:44:44'),
(15, 'Dumb Luck (Số Đỏ phiên bản tiếng Anh) - Vũ Trọng Phụng', 'dumb-luck-so-do-phien-ban-tieng-anh-vu-trong-phung', '<p>Dumb Luck l&agrave; bản dịch tiếng Anh của tiểu thuyết Số Đỏ của Vũ Trọng Phụng. T&aacute;c phẩm n&oacute;i về cuộc sống v&agrave; x&atilde; hội Việt Nam dưới g&oacute;c nh&igrave;n ch&acirc;m biếm v&agrave; sắc b&eacute;n.</p>', 'dumb-luck-so-do-phien-ban-tieng-anh-vu-trong-phung_173442181223.png', 1, 0, '2023-12-18 17:34:42', '2023-12-18 17:44:42'),
(16, 'Chiếc Lược Ngọc vàng - Hồ Biểu Chanh', 'chiec-luoc-ngoc-vang-ho-bieu-chanh', '<p>Chiếc Lược Ngọc v&agrave;ng l&agrave; một t&aacute;c phẩm của Hồ Biểu Chanh, m&ocirc; tả về cuộc sống th&ocirc;n qu&ecirc; Việt Nam v&agrave; những th&aacute;ch thức m&agrave; nh&acirc;n vật ch&iacute;nh phải đối mặt</p>', 'chiec-luoc-ngoc-vang-ho-bieu-chanh_173605181223.jpeg', 1, 0, '2023-12-18 17:36:05', '2023-12-18 17:44:41'),
(17, 'Truyện Kiều - Nguyễn Du', 'truyen-kieu-nguyen-du', '<p>Truyện Kiều l&agrave; một kiệt t&aacute;c của văn học cổ điển Việt Nam, s&aacute;ng t&aacute;c bởi Nguyễn Du. T&aacute;c phẩm n&oacute;i về cuộc đời v&agrave; t&igrave;nh cảm bi đạo của Kiều trong bối cảnh lịch sử phong kiến</p>', 'truyen-kieu-nguyen-du_173803181223.jpeg', 1, 0, '2023-12-18 17:38:03', '2023-12-18 17:44:40'),
(18, 'Sherlock Holmes Series - Arthur Conan Doyle', 'sherlock-holmes-series-arthur-conan-doyle', '<p>Bộ truyện nổi tiếng về th&aacute;m tử Sherlock Holmes v&agrave; người đồng đội trung th&agrave;nh của &ocirc;ng, b&aacute;c sĩ John Watson. C&acirc;u chuyện xoay quanh giải c&aacute;c vụ &aacute;n h&oacute;c b&uacute;a với sự sắc b&eacute;n v&agrave; logic của Sherlock Holmes</p>', 'sherlock-holmes-series-arthur-conan-doyle_173919181223.jpeg', 1, 0, '2023-12-18 17:39:19', '2023-12-18 17:44:39'),
(19, 'Agatha Christie\'s Poirot Series - Agatha Christie', 'agatha-christies-poirot-series-agatha-christie', '<p>T&aacute;c giả Agatha Christie đ&atilde; tạo ra nh&acirc;n vật Hercule Poirot, một nh&agrave; th&aacute;m tử t&agrave;i năng người Bỉ. C&aacute;c t&aacute;c phẩm trong loạt n&agrave;y thường đặt ra những c&acirc;u đố phức tạp v&agrave; kết th&uacute;c bất ngờ</p>', 'agatha-christies-poirot-series-agatha-christie_174020181223.jpeg', 1, 0, '2023-12-18 17:40:20', '2023-12-18 17:48:16'),
(20, 'Gone Girl - Gillian Flynn', 'gone-girl-gillian-flynn', '<p>Một cuộc t&igrave;m kiếm kỳ lạ bắt đầu khi Amy Dunne biến mất v&agrave;o ng&agrave;y kỷ niệm h&ocirc;n nh&acirc;n thứ năm của c&ocirc;. C&acirc;u chuyện xoay quanh c&aacute;c đ&aacute;m m&acirc;y tăm v&agrave; sự ph&acirc;n biệt giữa sự thật v&agrave; giả mạo</p>', 'gone-girl-gillian-flynn_174111181223.jpeg', 1, 0, '2023-12-18 17:41:11', '2023-12-18 17:48:14'),
(21, 'The Girl with the Dragon Tattoo - Stieg Larsson', 'the-girl-with-the-dragon-tattoo-stieg-larsson', '<p>Mikael Blomkvist v&agrave; Lisbeth Salander hợp t&aacute;c để giải quyết một vụ &aacute;n biến mất đ&atilde; k&eacute;o d&agrave;i nhiều năm trong gia đ&igrave;nh một doanh nh&acirc;n gi&agrave;u c&oacute; ở Thụy Điển. Bộ truyện n&agrave;y kết hợp giữa trinh th&aacute;m v&agrave; c&aacute;c yếu tố x&atilde; hội.</p>', 'the-girl-with-the-dragon-tattoo-stieg-larsson_174203181223.jpeg', 1, 0, '2023-12-18 17:42:03', '2023-12-18 17:48:14'),
(22, 'The Da Vinci Code - Dan Brown', 'the-da-vinci-code-dan-brown', '<p>Gi&aacute;o sư Robert Langdon v&agrave; Sophie Neveu phải giải một b&iacute; mật lớn li&ecirc;n quan đến Hội th&aacute;nh Đức Ch&uacute;a Trời khi một người t&igrave;m kiếm sự thật về b&iacute; ẩn của Điện Vật</p>', 'the-da-vinci-code-dan-brown_174308181223.jpeg', 1, 0, '2023-12-18 17:43:08', '2023-12-18 17:48:12'),
(23, 'In the Woods - Tana French', 'in-the-woods-tana-french', '<p>Một th&aacute;m tử quay trở lại qu&ecirc; nh&agrave; để giải quyết một vụ &aacute;n giết người li&ecirc;n quan đến qu&aacute; khứ của anh, nhưng khiến anh phải đối mặt với những b&iacute; mật kh&ocirc;ng mong muốn</p>', 'in-the-woods-tana-french_174400181223.jpeg', 1, 0, '2023-12-18 17:44:00', '2023-12-18 17:48:11');

